import{_ as r,c as p,m as e,a as o,t as s,U as a,o as i}from"./chunks/framework.UPTPDIz5.js";const n="/assets/Screenshot_16.fgpE43ib.png",l="/assets/Screenshot_15 (1).S-dxHxxH.png",d=JSON.parse('{"title":"Декабрь 2022","description":"","frontmatter":{"title":"Декабрь 2022"},"headers":[],"relativePath":"01.obnovleniya/2022-12.md","filePath":"01.obnovleniya/2022-12.md"}'),c={name:"01.obnovleniya/2022-12.md"},h={id:"frontmatter-title",tabindex:"-1"},k=e("a",{class:"header-anchor",href:"#frontmatter-title","aria-label":'Permalink to "{{ $frontmatter.title }}"'},"​",-1),w=a('<div class="info custom-block"><p class="custom-block-title">Рекомендация</p><p><strong>23.12.2022</strong></p></div><p>— добавлена метка <a href="https://support.1106.ru/spravochnik-wtw/wtw-metki/obshie-metki/specialnye-sluzhebnye/wp-is_phone" target="_blank" rel="noreferrer">wp = is_phone</a>, которая выводит содержимое блока на котором стоит только для мобильного телефона.</p><p>— добавлена метка <a href="https://support.1106.ru/spravochnik-wtw/wtw-metki/obshie-metki/specialnye-sluzhebnye/wp-is_tablet" target="_blank" rel="noreferrer">wp = is_tablet</a>, которая выводит содержимое блока на котором стоит только для планшета.</p><p>— добавлена метка <a href="https://support.1106.ru/spravochnik-wtw/wtw-metki/obshie-metki/specialnye-sluzhebnye/wp-is_mobile" target="_blank" rel="noreferrer">wp = is_mobile</a>, которая выводит содержимое блока на котором стоит только для мобильных устройств (планшет + телефон).</p><p>— добавлена метка <a href="https://support.1106.ru/spravochnik-wtw/wtw-metki/obshie-metki/specialnye-sluzhebnye/wp-is_desktop" target="_blank" rel="noreferrer">wp = is_desktop</a>, которая выводит содержимое блока на котором стоит только для десктопной(компьютерной) версии.</p><p>— добавлена метка <a href="https://support.1106.ru/spravochnik-wtw/wtw-metki/obshie-metki/specialnye-sluzhebnye/wp-is_ios" target="_blank" rel="noreferrer">wp = is_ios</a>, которая выводит содержимое блока на котором стоит только для устройств с операционной системой iOS.</p><p>— добавлена метка <a href="https://support.1106.ru/spravochnik-wtw/wtw-metki/obshie-metki/specialnye-sluzhebnye/wp-is_android" target="_blank" rel="noreferrer">wp = is_android</a>, которая выводит содержимое блока на котором стоит только для устройств с операционной системой Android.</p><div class="info custom-block"><p class="custom-block-title">Рекомендация</p><p><strong>21.12.2022</strong></p></div><p>— добавлен пакет меток поддержки произвольных полей товара на странице корзины. Среди поддерживаемых меток: c_text, c_link, c_image, c_bg_image и c_loop. <a href="https://support.1106.ru/spravochnik-wtw/wtw-metki/proizvolnye-polya/polya-korziny" target="_blank" rel="noreferrer">Подробнее.</a></p><div class="info custom-block"><p class="custom-block-title">Рекомендация</p><p><strong>20.12.2022</strong></p></div><p>— добавлена метка <a href="https://support.1106.ru/spravochnik-wtw/wtw-metki/obshie-metki/specialnye-sluzhebnye/wp-parent_title_link" target="_blank" rel="noreferrer">wp = parent_title_link</a>, для вывода заголовка и ссылки родительской страницы.</p><p>— добавлена метка <a href="https://support.1106.ru/spravochnik-wtw/wtw-metki/woocommerce-magazin/korzina/obshie-polya/wp-cart_volume" target="_blank" rel="noreferrer">wp = cart_volume</a>. Метка подсчитывает и выводит общий объем товара в корзине.</p><div class="info custom-block"><p class="custom-block-title">Рекомендация</p><p><strong>19.12.2022</strong></p></div><p>— создана метка <a href="https://support.1106.ru/spravochnik-wtw/wtw-metki/taksonomii/polya-termov/tekstovye/wp_count" target="_blank" rel="noreferrer">wp_count</a>, для вывода количества записей в терме вместе со склоняемыми наименованиями.</p><div class="info custom-block"><p class="custom-block-title">Рекомендация</p><p><strong>18.12.2022</strong></p></div><p>— создана метка <a href="https://support.1106.ru/spravochnik-wtw/wtw-metki/proizvolnye-polya/polya-zapisei/tekhnicheskie/row-index" target="_blank" rel="noreferrer">row = index</a>, которая выводит индекс текущего элемента в потворителе acf.</p><p>— создана метка <a href="https://support.1106.ru/spravochnik-wtw/wtw-metki/obshie-metki/specialnye-sluzhebnye/php_if" target="_blank" rel="noreferrer">php_if</a> для проверки выполнения условия, указанного в качестве передаваемого параметра. </p><p>— создана метка <a href="https://support.1106.ru/spravochnik-wtw/wtw-metki/obshie-metki/specialnye-sluzhebnye/php_elseif" target="_blank" rel="noreferrer">php_elseif</a> для проверки выполнения дополнительного условия, указанного в качестве передаваемого параметра. </p><p>— создана метка <a href="https://support.1106.ru/spravochnik-wtw/wtw-metki/obshie-metki/specialnye-sluzhebnye/php-else" target="_blank" rel="noreferrer">php=else</a> для вывода альтернативного блока, в случае не выполнения указанных в метках php_if и php_elseif условий. </p><p>— создана метка <a href="https://support.1106.ru/spravochnik-wtw/wtw-metki/obshie-metki/specialnye-sluzhebnye/php_end-if" target="_blank" rel="noreferrer">php_end=if</a> для завершения ветви условий. </p><p>— создана метка <a href="https://support.1106.ru/spravochnik-wtw/wtw-metki/obshie-metki/vstavka-koda/php_before" target="_blank" rel="noreferrer">php_before</a>, позволяющая вывести php код перед блоком на котором установлена. </p><p>— создана метка <a href="https://support.1106.ru/spravochnik-wtw/wtw-metki/obshie-metki/vstavka-koda/php_after" target="_blank" rel="noreferrer">php_after</a>, позволяющая вывести php код после блока на котором установлена. </p><p>— создана метка <a href="https://support.1106.ru/spravochnik-wtw/wtw-metki/obshie-metki/vstavka-koda/php_echo" target="_blank" rel="noreferrer">php_echo</a>, позволяющая вывести php код внутри блока на котором установлена.</p><p>— создана метка <a href="https://support.1106.ru/spravochnik-wtw/wtw-metki/obshie-metki/vstavka-koda/php" target="_blank" rel="noreferrer">php</a>, позволяющая вывести php код вместо блока на котором установлена.</p><p>— создана метка <a href="https://support.1106.ru/spravochnik-wtw/wtw-metki/woocommerce-magazin/prostoi-tovar/drugoe/wp_if-price_sale" target="_blank" rel="noreferrer">wp_if=price_sale</a>, которая проверяет наличие скидки у текущего товара.</p><div class="info custom-block"><p class="custom-block-title">Рекомендация</p><p><strong>14.12.2022</strong></p></div><p>— добавлена новая метка <a href="https://support.1106.ru/spravochnik-wtw/wtw-metki/proizvolnye-polya/polya-zapisei/vstavka-koda/f_code" target="_blank" rel="noreferrer">f_code</a>, которая позволяет создать поле ввода кода.</p><div class="info custom-block"><p class="custom-block-title">Рекомендация</p><p><strong>03.12.2022</strong></p></div><p>— в раздел &quot;Общий код&quot; Конфигуратора сайта добавлен новый блок для ввода кода сразу после открывающего тега &lt;body&gt;. Также изменены сами поля ввода, в них добавлена подсветка синтаксиса.</p><figure><img src="'+n+'" alt=""><figcaption></figcaption></figure><div class="info custom-block"><p class="custom-block-title">Рекомендация</p><p><strong>02.12.2022</strong></p></div><p>— в параметры отправки форм (конфигуратор сайта) добавлена возможность указать список имен полей для валидации. Если список пуст, то срабатывают обязательные поля заданные на webflow. Если обязательные поля заданы и на webflow и в параметрах отправки, то последние будут работать в качестве подстраховки. И наконец-то, если на webflow валидация не указана, а перечень полей прописан в конфигураторе, то до их заполнения возможность отправки формы будет недоступна.</p><figure><img src="'+l+'" alt=""><figcaption></figcaption></figure>',33);function _(t,f,m,u,b,v){return i(),p("div",null,[e("h1",h,[o(s(t.$frontmatter.title)+" ",1),k]),w])}const y=r(c,[["render",_]]);export{d as __pageData,y as default};
