import{_ as a,c as e,m as o,a as i,t as r,U as c,o as s}from"./chunks/framework.UPTPDIz5.js";const g=JSON.parse('{"title":"Июль 2023","description":"","frontmatter":{"title":"Июль 2023"},"headers":[],"relativePath":"01.obnovleniya/2023-07.md","filePath":"01.obnovleniya/2023-07.md"}'),n={name:"01.obnovleniya/2023-07.md"},p={id:"frontmatter-title",tabindex:"-1"},l=o("a",{class:"header-anchor",href:"#frontmatter-title","aria-label":'Permalink to "{{ $frontmatter.title }}"'},"​",-1),m=c('<div class="info custom-block"><p class="custom-block-title">Рекомендация</p><p><strong>14.07.2023</strong></p></div><p>Добавлена новая метка — <a href="./../wtw-metki/woocommerce-magazin/korzina/obshie-polya/wp-coupon_discount.html">wp=coupon_discount</a>, которая выводит общую сумму скидок по всем примененным в корзине промокодам. </p><p>Добавлена новая метка — <a href="./../wtw-metki/woocommerce-magazin/korzina/obshie-polya/wp-discount_amount.html">wp=discount_amount</a>, которая выводит общую сумму скидок на товары в корзине. </p><p>Добавлена новая метка — <a href="./../wtw-metki/woocommerce-magazin/korzina/obshie-polya/wp-discount_percent.html">wp=discount_percent</a>, позволяющая получить общий процент скидок от всей суммы на товары в корзине. </p><p>Добавлена новая метка — <a href="./../wtw-metki/woocommerce-magazin/korzina/spisok-tovarov/wp_cart-if_low_stock.html">wp_cart=if_low_stock</a>, проверяющая находятся ли остатки текущего товара в корзине в пределах установленной границы малых запасов. </p><p>Добавлена новая метка — <a href="./../wtw-metki/woocommerce-magazin/korzina/spisok-tovarov/wp_cart-low_stock.html">wp_cart=low_stock</a>, которая выводит число остатков текущего товара в корзине, если его количество находится в пределах установленной границы малых запасов. </p><p>Добавлена новая метка — <a href="./../wtw-metki/woocommerce-magazin/prostoi-tovar/nalichie-na-sklade/wp_if-low_stock.html">wp_if=low_stock</a>, проверяющая находятся ли остатки текущего простого товара, на персональной странице данного товара, в пределах установленной границы малых запасов. </p><p>Добавлена новая метка — <a href="./../wtw-metki/woocommerce-magazin/prostoi-tovar/nalichie-na-sklade/wp-low_stock.html">wp=low_stock</a>, которая выводит число остатков текущего товара, на персональной странице этого товара, если его количество находится в пределах установленной границы малых запасов.</p><div class="info custom-block"><p class="custom-block-title">Рекомендация</p><p><strong>01.07.2023</strong></p></div><p>— добавлена метка <a href="./../wtw-metki/proizvolnye-polya/polya-zapisei/izobrazheniya/f_image_gallery.html">f_image_gallery</a>, для вывода галереи изображений без лайтбокса.</p>',10);function _(t,w,h,k,d,f){return s(),e("div",null,[o("h1",p,[i(r(t.$frontmatter.title)+" ",1),l]),m])}const v=a(n,[["render",_]]);export{g as __pageData,v as default};
