import{_ as p,c as s,m as e,a as i,t as a,U as r,o}from"./chunks/framework.UPTPDIz5.js";const c="/assets/Screenshot_3 (2).zChQ6Z77.png",g=JSON.parse('{"title":"php_if","description":"","frontmatter":{"title":"php_if"},"headers":[],"relativePath":"01.wtw-metki/obshie-metki/specialnye-sluzhebnye/php_if.md","filePath":"01.wtw-metki/obshie-metki/specialnye-sluzhebnye/php_if.md"}'),l={name:"01.wtw-metki/obshie-metki/specialnye-sluzhebnye/php_if.md"},n={id:"frontmatter-title",tabindex:"-1"},h=e("a",{class:"header-anchor",href:"#frontmatter-title","aria-label":'Permalink to "{{ $frontmatter.title }}"'},"​",-1),_=r('<div class="tip custom-block"><p class="custom-block-title">Инструкция</p><p>php_if = условие на языке php</p></div><p>Метка используется для проверки выполнения условия указанного в качестве передаваемого параметра.</p><p>В отличии от метки <a href="./wp_if.html">wp_if</a> способна взаимодействовать с условными операторами <code>else if</code> и <code>else</code>. </p><div class="tip custom-block"><p class="custom-block-title">Инструкция</p><p>Метка устанавливается на блок, который необходимо отобразить, в случае выполнения указанного условия.</p></div><p>Если задача требует в случае невыполнения текущего условия в метке, сделать проверку по следующего условию, используется метка <a href="https://support.1106.ru/spravochnik-wtw/wtw-metki/obshie-metki/specialnye-sluzhebnye/php_elseif" target="_blank" rel="noreferrer">php_elseif</a>.</p><p>Если в результате невыполнения текущего условия в метке требуется вывести другой блок, используется метка <a href="https://support.1106.ru/spravochnik-wtw/wtw-metki/obshie-metki/specialnye-sluzhebnye/php-else" target="_blank" rel="noreferrer">php=else</a>.</p><div class="warning custom-block"><p class="custom-block-title">Замечание</p><p>Каждая созданная метка php_if обязательно должна включать в себя закрывающую метку <a href="https://support.1106.ru/spravochnik-wtw/wtw-metki/obshie-metki/specialnye-sluzhebnye/php_end-if" target="_blank" rel="noreferrer">php_end=if</a>. Иначе возникнет ошибка!</p></div><p><strong>ПРИМЕР:</strong></p><figure><img src="'+c+'" alt=""><figcaption></figcaption></figure><p>В текущем примере проверяется есть ли что-то в поле с именем &quot;cena_vse&quot;. Если поле не пустое, то условие выполнится и блок на котором стоит метка отобразится.</p>',10);function f(t,m,d,k,u,b){return o(),s("div",null,[e("h1",n,[i(a(t.$frontmatter.title)+" ",1),h]),_])}const y=p(l,[["render",f]]);export{g as __pageData,y as default};
