import{_ as a,c as e,m as o,a as r,t as s,U as n,o as i}from"./chunks/framework.UPTPDIz5.js";const c="/assets/klassy-dostavki-woocommerce.45yui4UH.png",l="/assets/dobavljaem-klass-dostavki-k-tovaru (1).sdXyN3jg.png",p="/assets/nastrojki-dostavki-edinaja-stavka-woocommerce.IyzKLR_n.png",w=JSON.parse('{"title":"Бесплатная доставка только для определённых товаров","description":"","frontmatter":{"title":"Бесплатная доставка только для определённых товаров"},"headers":[],"relativePath":"05.instrukcii/woocommerce/instrukcii/besplatnaya-dostavka-tolko-dlya-opredelyonnykh-tovarov.md","filePath":"05.instrukcii/woocommerce/instrukcii/besplatnaya-dostavka-tolko-dlya-opredelyonnykh-tovarov.md"}'),m={name:"05.instrukcii/woocommerce/instrukcii/besplatnaya-dostavka-tolko-dlya-opredelyonnykh-tovarov.md"},d={id:"frontmatter-title",tabindex:"-1"},g=o("a",{class:"header-anchor",href:"#frontmatter-title","aria-label":'Permalink to "{{ $frontmatter.title }}"'},"​",-1),h=n('<p>Создать и настроить бесплатный способ доставки в WooCommerce у вас не должно составить труда, а вот как применить бесплатную доставку только к определённым товарам, да ещё и без использования кода?</p><h3 id="шаг-1-создаем-класс-доставки-woocommerce" tabindex="-1">Шаг 1. Создаём класс доставки WooCommerce <a class="header-anchor" href="#шаг-1-создаем-класс-доставки-woocommerce" aria-label="Permalink to &quot;Шаг 1. Создаём класс доставки WooCommerce&quot;">​</a></h3><p>Для этого переходим в настройки WooCommerce, затем <strong>Доставка &gt; Классы доставки.</strong></p><figure><img src="'+c+'" alt=""><figcaption><p>Мы создали свой класс доставки в настройках WooCommerce.</p></figcaption></figure><p>При создании в качестве названия / ярлыка / описания можете указывать какие-то свои произвольные данные для своего удобства.</p><h3 id="шаг-2-присваиваем-класс-доставки-к-нужным-товарам" tabindex="-1">Шаг 2. Присваиваем класс доставки к нужным товарам <a class="header-anchor" href="#шаг-2-присваиваем-класс-доставки-к-нужным-товарам" aria-label="Permalink to &quot;Шаг 2. Присваиваем класс доставки к нужным товарам&quot;">​</a></h3><p>Это делается при редактировании конкретных товаров. Переходим в настройки товара и там в метабоксе <strong>Данные товара,</strong> во вкладке <strong>Доставка</strong> всё и происходит.</p><figure><img src="'+l+'" alt=""><figcaption><p>Тут мы добавляем созданный нами в первом шаге класс доставки к какому-то товару, на странице его редактирования.</p></figcaption></figure><h3 id="шаг-3-настраиваем-бесплатную-доставку-для-товаров" tabindex="-1">Шаг 3. Настраиваем бесплатную доставку для товаров <a class="header-anchor" href="#шаг-3-настраиваем-бесплатную-доставку-для-товаров" aria-label="Permalink to &quot;Шаг 3. Настраиваем бесплатную доставку для товаров&quot;">​</a></h3><p>Теперь нужно перейти настройки способа доставки «Единая ставка», для этого переходим в <strong>Настройки &gt; Доставка &gt; Зоны доставки,</strong> выбираем зону доставки, и в ней уже переходим в настройки способа.</p><figure><img src="'+p+'" alt=""><figcaption><p>Так как мы хотим сделать именно бесплатную доставку, то в поле Стоимость важно указать 0, а дальше уже задаём цены для каждого существующего класса доставки отдельно.</p></figcaption></figure><p>Теперь давайте разберёмся с настройками на скриншоте и тем, как будет вычисляться доставка.</p><ul><li>Тип вычисления <strong>«На класс»:</strong> Стоимость + Сумма стоимости каждого класса в корзине.</li><li>Тип вычисления <strong>«На заказ»:</strong> Стоимость + Стоимость самого дорогого класса в корзине.</li></ul><p>«Нет цен классов доставки» – эта та стоимость доставки, которая будет применяться, если мы не добавили товаров с классом «Беспл доставка» в корзину.</p><p><strong>Источник:</strong> <a href="https://misha.agency/woocommerce/besplatnaya-dostavka-dlya-opredelyonnyh-tovarov.html" target="_blank" rel="noreferrer"><strong>https://misha.agency/woocommerce/besplatnaya-dostavka-dlya-opredelyonnyh-tovarov.html</strong></a></p>',15);function _(t,k,y,f,u,v){return i(),e("div",null,[o("h1",d,[r(s(t.$frontmatter.title)+" ",1),g]),h])}const T=a(m,[["render",_]]);export{w as __pageData,T as default};
