import{_ as i,c as l,m as t,a as o,t as s,U as n,o as g}from"./chunks/framework.UPTPDIz5.js";const b=JSON.parse('{"title":"Горячие клавиши в WordPress","description":"","frontmatter":{"title":"Горячие клавиши в WordPress"},"headers":[],"relativePath":"05.instrukcii/wordpress/goryachie-klavishi-v-wordpress.md","filePath":"05.instrukcii/wordpress/goryachie-klavishi-v-wordpress.md"}'),a={name:"05.instrukcii/wordpress/goryachie-klavishi-v-wordpress.md"},e={id:"frontmatter-title",tabindex:"-1"},h=t("a",{class:"header-anchor",href:"#frontmatter-title","aria-label":'Permalink to "{{ $frontmatter.title }}"'},"​",-1),d=n('<h3 id="основные-сочетания-клавиш" tabindex="-1">Основные сочетания клавиш <a class="header-anchor" href="#основные-сочетания-клавиш" aria-label="Permalink to &quot;Основные сочетания клавиш&quot;">​</a></h3><p>Для начала мы рассмотрим несколько полезных сочетаний клавиш для обычных задач по форматированию текста. Приведенные ниже примеры работают как для редактора блоков Gutenberg, так и для классического редактора.</p><h4 id="форматирование-для-пользователеи-windows" tabindex="-1">Форматирование для пользователей Windows <a class="header-anchor" href="#форматирование-для-пользователеи-windows" aria-label="Permalink to &quot;Форматирование для пользователей Windows&quot;">​</a></h4><ul><li><strong>Ctrl + C</strong> – скопировать выделенный текст</li><li><strong>Ctrl + V</strong> – вставить скопированный текст</li><li><strong>Ctrl + Shift + V</strong> – вставить скопированный текст без форматирования</li><li><strong>Ctrl + X</strong> – вырезать выделенный текст</li><li><strong>Ctrl + B</strong> – сделать жирным выделенный текст</li><li><strong>Ctrl + I</strong> – сделать курсивным выделенный текст</li><li><strong>Ctrl + U</strong> – подчеркнуть текст</li><li><strong>Ctrl + A</strong> – выделить весь текст</li><li><strong>Ctrl + Z</strong> – отменить последнее действие</li><li><strong>Ctrl + K</strong> – вставить/править ссылку</li><li><strong>Ctrl + S</strong> – сохранить страницу/запись</li><li><strong>Ctrl + F</strong> – найти текст на странице</li></ul><h4 id="форматирование-для-пользователеи-mac" tabindex="-1">Форматирование для пользователей Mac <a class="header-anchor" href="#форматирование-для-пользователеи-mac" aria-label="Permalink to &quot;Форматирование для пользователей Mac&quot;">​</a></h4><ul><li><strong>Cmd</strong> <strong>+ C</strong> – скопировать выделенный текст</li><li><strong>Cmd</strong> <strong>+ V</strong> – вставить скопированный текст</li><li><strong>Cmd + Shift + V</strong> – вставить скопированный текст без форматирования</li><li><strong>Cmd</strong> <strong>+ X</strong> – вырезать выделенный текст</li><li><strong>Cmd + B</strong> – сделать жирным выделенный текст</li><li><strong>Cmd</strong> <strong>+ I</strong> – сделать курсивным выделенный текст</li><li><strong>Cmd</strong> <strong>+ U</strong> – подчеркнуть текст</li><li><strong>Cmd + A</strong> – выделить весь текст</li><li><strong>Cmd + Z</strong> – отменить последнее действие</li><li><strong>Cmd + K</strong> – вставить/править ссылку</li><li><strong>Cmd + S</strong> – сохранить страницу/запись</li><li><strong>Cmd + F</strong> – найти текст на странице</li></ul><h3 id="горячие-клавиши-для-gutenberg" tabindex="-1">Горячие клавиши для <strong>Gutenberg</strong> <a class="header-anchor" href="#горячие-клавиши-для-gutenberg" aria-label="Permalink to &quot;Горячие клавиши для **Gutenberg**&quot;">​</a></h3><p>Если вы используете редактор блоков WordPress, то вам пригодятся следующие сочетания клавиш.</p><h4 id="комбинации-клавиш-gutenberg-для-windows" tabindex="-1">Комбинации клавиш Gutenberg для Windows <a class="header-anchor" href="#комбинации-клавиш-gutenberg-для-windows" aria-label="Permalink to &quot;Комбинации клавиш Gutenberg для Windows&quot;">​</a></h4><ul><li><strong>/</strong> – выбрать новый блок (использовать на пустом параграфе)</li><li><strong>Ctrl + Shift + D</strong> — дублировать выделенный блок</li><li><strong>Alt + Shift + Z</strong> — удалить блок вместе с содержимым</li><li><strong>Ctrl + Alt + T</strong> – вставить блок перед выделенным блоком</li><li><strong>Ctrl + Alt + Y</strong> – вставить блок после выделенного блока</li><li><strong>Ctrl + Shift + Alt + T</strong> — переместить блок выше</li><li><strong>Ctrl + Shift + Alt + Y</strong> — переместить блок ниже</li><li><strong>Ctrl + Shift+ K</strong> — удалить ссылку</li><li><strong>Ctrl + Shift + Z</strong> — повторить последнее отмену</li><li><strong>Ctrl + Shift + Alt + M</strong> — переключиться между визуальным и текстовым режимами</li><li><strong>Ctrl + Shift + Alt + F</strong> — переключить полноэкранный режим</li><li><strong>Ctrl + Shift + ,</strong> — выключить/включить боковую колонку</li><li><strong>Shift + Alt + N</strong> — перейти к следующей части пользовательского интерфейса</li><li><strong>Shift + Alt + P</strong> — перейти к предыдущей части пользовательского интерфейса</li><li><strong>Esc</strong> — снять выделение</li></ul><h4 id="комбинации-клавиш-gutenberg-для-mac" tabindex="-1">Комбинации клавиш Gutenberg для Mac <a class="header-anchor" href="#комбинации-клавиш-gutenberg-для-mac" aria-label="Permalink to &quot;Комбинации клавиш Gutenberg для Mac&quot;">​</a></h4><ul><li><strong>/</strong> – выбрать новый блок (использовать на пустом параграфе)</li><li><strong>Cmd + Shift + D</strong> — дублировать выделенный блок</li><li><strong>Ctrl + Opt + Z</strong> — удалить блок вместе с содержимым</li><li><strong>Cmd + Opt + T</strong> – вставить блок перед выделенным блоком</li><li><strong>Cmd + Opt + Y</strong> – вставить блок после выделенного блока</li><li><strong>Cmd + Shift + Opt + T</strong> — переместить блок выше</li><li><strong>Cmd + Shift + Opt + Y</strong> — переместить блок ниже</li><li><strong>Cmd + Shift+ K</strong> — удалить ссылку</li><li><strong>Cmd + Shift + Z</strong> — повторить последнее отмену</li><li><strong>Cmd + Shift + Opt + M</strong> — переключиться между визуальным и текстовым режимами</li><li><strong>Cmd + Shift + Opt + F</strong> — переключить полноэкранный режим</li><li><strong>Cmd + Shift + ,</strong> — выключить/включить боковую колонку</li><li><strong>Ctrl + Opt + N</strong> — перейти к следующей части пользовательского интерфейса</li><li><strong>Ctrl + Opt + P</strong> — перейти к предыдущей части пользовательского интерфейса</li><li><strong>Esc</strong> — снять выделение</li></ul><p>Вы также можете найти этот список в меню редактора Gutenberg в разделе «Горячие клавиши». Чтобы найти его, просто нажмите на значок с тремя точками в правом верхнем углу (или используйте <code>Alt + Shift + H</code> в Windows и <code>Cmd + Opt + H</code> на Mac).</p><h3 id="горячие-клавиши-для-классического-редактора" tabindex="-1">Горячие клавиши для классического редактора <a class="header-anchor" href="#горячие-клавиши-для-классического-редактора" aria-label="Permalink to &quot;Горячие клавиши для классического редактора&quot;">​</a></h3><p>Теперь мы переходим к комбинациям клавиш, которые работают исключительно в классическом редакторе.</p><h4 id="для-пользователеи-windows" tabindex="-1">Для пользователей Windows <a class="header-anchor" href="#для-пользователеи-windows" aria-label="Permalink to &quot;Для пользователей Windows&quot;">​</a></h4><ul><li><strong>Alt + Shift + M</strong> — вставить изображение</li><li><strong>Alt + Shift + S</strong> – удалить ссылку</li><li><strong>Alt + Shift + L</strong> — выровнять выделенный текст слева</li><li><strong>Alt + Shift + R</strong> — выровнять выделенный текст справа</li><li><strong>Alt + Shift + C</strong> – выровнять выделенный текст по центру</li><li><strong>Alt + Shift + D</strong> – перечеркнуть выделенный текст</li><li><strong>Alt + Shift + O</strong> — вставить нумерованный список</li><li><strong>Alt + Shift + U</strong> — вставить маркированный список</li><li><strong>Alt + Shift + T</strong> – вставить тег «Читать далее»</li><li><strong>Alt + Shift + Q</strong> – вставить цитату</li><li><strong>Alt + Shift + #</strong> — преобразовать выделенный текст в Заголовок (2 = h2, 3 = h3, 4 = h4, 5 = h5, 6 = h6</li></ul><h4 id="для-пользователеи-mac" tabindex="-1">Для пользователей Mac <a class="header-anchor" href="#для-пользователеи-mac" aria-label="Permalink to &quot;Для пользователей Mac&quot;">​</a></h4><ul><li><strong>Ctrl + Opt + M</strong> — вставить изображение</li><li><strong>Ctrl + Opt + S</strong> — удалить ссылку</li><li><strong>Ctrl + Opt + L</strong> – выровнять выделенный текст слева</li><li><strong>Ctrl + Opt + R</strong> – выровнять выделенный текст справа</li><li><strong>Ctrl + Opt + C</strong> — выровнять выделенный текст по центру</li><li><strong>Ctrl + Opt + D</strong> – перечеркнуть выделенный текст</li><li><strong>Ctrl + Opt + O</strong> — вставить нумерованный список</li><li><strong>Ctrl + Opt + U</strong> — вставить маркированный список</li><li><strong>Ctrl + Opt + T</strong> – вставить тег «Читать далее»</li><li><strong>Ctrl + Opt + Q</strong> – вставить цитату</li><li><strong>Ctrl + Opt + #</strong> — преобразовать выделенный текст в Заголовок (2 = h2, 3 = h3, 4 = h4, 5 = h5, 6 = h6</li></ul>',19);function c(r,C,f,m,u,p){return g(),l("div",null,[t("h1",e,[o(s(r.$frontmatter.title)+" ",1),h]),d])}const _=i(a,[["render",c]]);export{b as __pageData,_ as default};
